import { Button, TextField, Box, Alert } from '@mui/material';
//import InputMask from "react-input-mask";


import { useForm } from "react-hook-form";
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from "yup";
import { useState } from 'react';



export default function Form() {

    const [data, setData] = useState()
    const { register, handleSubmit, formState: { errors } } = useForm({});

    console.log(data)

    const onSubmit = data => setData(data);


    return (
        <Box
            sx={{ 
                borderRadius: '10px', 
                display: 'flex', 
                flexDirection: 'column', 
                gap: '30px', 
                background: '#3db0f7', 
                background: 'lightblue', 
                width: '90%', 
                padding: '20px',
                margin:'20px auto' 
                }}>
            <Box sx={{ fontSize: '48px', color: "#ffffff", textAlign: 'center' }}>Solicite seu or√ßamento</Box>
            <form onSubmit={handleSubmit(onSubmit)}>
                <Box sx={{ display: 'flex', gap: "20px" }}>

                    <TextField
                        sx={{ width: '100%', background: "#ffffff", borderRadius: '10px', }}
                        name='nome'
                        required
                        id="outlined-required"
                        label="Nome"
                        {...register("nome")}
                    />

                    <TextField
                        sx={{ width: '100%', background: "#ffffff", borderRadius: '10px' }}
                        name='sobreNome'
                        required
                        id="outlined-required"
                        label="Sobrenome"
                        {...register("sobreNome")}

                    />

                </Box>
                <Box sx={{ display: 'flex', gap: "20px", marginTop: '20px', marginBottom: '20px' }}>
                    <TextField
                        sx={{ width: '100%', background: "#ffffff", borderRadius: '10px', }}
                        name='email'
                        required
                        id="outlined-required"
                        label="Email"
                        {...register("email")}
                    />

                    <TextField
                        sx={{ width: '100%', background: "#ffffff", borderRadius: '10px', }}
                        name='telefone'
                        required
                        id="outlined-required"
                        type='number'
                        label="Telefone"
                        {...register("telefone")}
                    />

                </Box>


                <TextField
                    sx={{ width: '100%', background: "#ffffff", borderRadius: '10px', }}
                    id="outlined-multiline-static"
                    name='mensagem'
                    label="Mensagem"
                    multiline
                    rows={4}
                    {...register("mensagem")}
                />
                {data ?
                    <Alert sx={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}
                        severity="success">Mensagem enviada com sucesso!</Alert>
                    :

                    <Box sx={{ display: 'flex', justifyContent: 'end', marginTop: '20px' }}>
                        <Button type='submit' sx={{ background: '#ffffff', height: '50px', width: '30%' }}>Enviar</Button>
                    </Box>
                }


            </form>
        </Box>
    )
}
